import React, { Component } from 'react'
import CartService from '../../../service/cart.service'

import './CartList.css'


import Container from 'react-bootstrap/Container'
import Table from 'react-bootstrap/Table'
import Row from 'react-bootstrap/Row'

import { Link } from 'react-router-dom'

class CartList extends Component {

    constructor(props) {
        super(props)
        this.state = {
            cart: this.props.cart
        }
        this.cartService = new CartService()
    }

    getCartInfo = () =>{
        this.cartService.getCart()
            .then(response => this.setState({ cart : response.data}))
            .catch(err => console.log(err))
    }

    componentDidMount = () => {
        // this.getCartInfo()
    }
    sum = () => {
        {this.state.cart[0].product.price}
    }
    render() {

        return (
            <Container as="section">
                <Row style={{justifyContent: 'center', }}>
                    <Row style={{margin: '10%'}}>
                        <h1>Your Products </h1>
                    </Row>
                        {this.state.cart[0].product.img}
                    <Table bordered >
                        <thead>
                            <tr>
                                <th>Product</th>
                                <th>Unit Price</th>
                                <th>Quantity</th>
                                <th>Subtotal</th>
                            </tr>
                        </thead>
                        <tbody> 
                            <tr>
                                <th></th>
                                <th>{this.state.cart[0].product.count}</th>
                                <th></th>
                                <th></th>
                            </tr>
                            {/* {
                                this.props.cart &&
                                <>
                                    {this.props.cart.map((elm) => this.setState({product: elm.img}))}
                                </>
                            } */}

                        </tbody>
                    </Table>
                </Row>
                <Link to="/products" className="btn btn-dark">Products</Link>

            </Container>
        )
    }
}

export default CartList
