import React, { Component } from "react";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";

import AuthService from "./../../../service/auth.service";

import { Link } from "react-router-dom";

class Navigation extends Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.authService = new AuthService();
  }

  logout = () => {
    this.props.setTheUser(false);
    this.authService.logout();
  };

  getCartCount = () => {
    const product = this.props.cart.filter((elm) => elm.id === this.state._id);
    console.log(product);
    if (product.length) {
      return product[0].count;
    } else {
      return 0;
    }
  };

  sumCount = () => {
    const totalProducts = this.props.cart.reduce(
      (cv, total) => (total += cv.count)
    );
    return totalProducts;
  };
  render() {
    console.log(this.props.cart);

    return (
      <Navbar bg="dark" variant="dark" expand="md">
        <Navbar.Brand as="div">
          <Link to="/">KIZENO SHOP</Link>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav>
            <Nav.Link as="div">
              <Link to="/">Home</Link>
            </Nav.Link>
            <Nav.Link as="div">
              <Link to="/products">Products</Link>
            </Nav.Link>

            {!this.props.loggedInUser ? (
              <>
                <Nav.Link as="div">
                  <Link to="/login">Login</Link>
                </Nav.Link>
                <Nav.Link as="div">
                  <Link to="/signup">Signup</Link>
                </Nav.Link>
              </>
            ) : (
              <>
                <Nav.Link as="div">
                  <Link to="/profile">My Profile</Link>
                </Nav.Link>
                <Nav.Link as="div" onClick={this.logout}>
                  Logout
                </Nav.Link>
              </>
            )}
          </Nav>
          <Navbar.Text className="ml-auto">
            {" "}
            Hello,{" "}
            {this.props.loggedInUser
              ? this.props.loggedInUser.username
              : "Guest"}
          </Navbar.Text>
          {!this.props.cart.length ? (
            <>
              <Navbar.Text className="ml-auto">
                {" "}
                CART {this.sumCount()}{" "}
              </Navbar.Text>
            </>
          ) : (
            <>
              <Navbar.Text className="ml-auto">
                {" "}
                CART {this.sumCount()}{" "}
              </Navbar.Text>
            </>
          )}
        </Navbar.Collapse>
      </Navbar>
    );
  }
}

export default Navigation;
