import React, { Component } from 'react'
import CartService from '../../../service/cart.service'

import './CartList.css'


import Container from 'react-bootstrap/Container'
import Table from 'react-bootstrap/Table'
import Row from 'react-bootstrap/Row'

import { Link } from 'react-router-dom'

class CartList extends Component {

    constructor(props) {
        super(props)
        this.state = {
            cart: this.props.cart
        }
        this.cartService = new CartService()
    }

    getCartInfo = () =>{
        this.cartService.getCart()
            .then(response => this.setState({ cart : response.data}))
            .catch(err => console.log(err))
    }

    componentDidMount = () => {
        // this.getCartInfo()
    }

    render() {

        return (
          <Container as="section">
            {/* <Row style={{ justifyContent: "center" }}> */}
              <Row style={{justifyContent: 'center'}}>
                <h1>C · a · r · t</h1>
              </Row>
              <hr/>
              <Link to="/products" className='button back-btn' style={{marginB}}>Products</Link>
              <Table className="table">
                <thead>
                  <tr>
                    <th>Product</th>
                    <th>Product Name</th>
                    <th>Unit Price</th>
                    <th>Quantity</th>
                    <th>Subtotal</th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.cart.map((elm, idx) => (
                    <tr key={idx}>
                      <th>
                        <Link to={`/products/oneProduct/${elm._id}`}>
                          <img
                            className="product-picture"
                            src={elm.product.img}
                            alt={elm.product.name}
                          />
                        </Link>
                      </th>
                      <th>
                        <p style={{ marginTop: "65px" }}>{elm.product.name}</p>
                      </th>
                      <th>
                        <p style={{ marginTop: "65px" }}>{elm.product.price}</p>
                      </th>
                      <th>
                        <p style={{ marginTop: "65px" }}>{elm.count}</p>
                      </th>
                      <th>
                        <p style={{ marginTop: "65px" }}>{elm.price}</p>
                      </th>
                    </tr>
                  ))}
                  {/* <tr>
                                <th><img src={this.state.cart[1].product.img} alt={this.state.name} /></th>
                                <th>{this.state.cart[0].count}</th>
                                <th></th>
                                <th></th>
                            </tr> */}
                  {/* {
                                this.props.cart &&
                                <>
                                    {this.props.cart.map((elm) => this.setState({product: elm.img}))}
                                </>
                            } */}
                </tbody>

              </Table>
            {/* </Row> */}
          </Container>
        );
    }
}

export default CartList
