import React, { Component } from 'react'
import CartService from '../../../service/cart.service'

import './CartList.css'


import Container from 'react-bootstrap/Container'
import Table from 'react-bootstrap/Table'
import Row from 'react-bootstrap/Row'

import { Link } from 'react-router-dom'


class CartList extends Component {

    constructor(props) {
        super(props)
        this.state = {
            products: [],
            quantity: null,
            total: null
        }
        this.cartService = new CartService()
    }

    getCartInfo = () =>{
        const userId = this.props.loggedInUser._id
        this.cartService.getCart(userId)
            .then(response => this.setState({
                products: response.data.products
            }))
            .catch(err => console.log(err))
    }

    updateProducts = () => {
        const productsUpdated = [...this.props.cart]
        this.setState({productsUpdated})
    }

    componentDidMount = () => {
        this.getCartInfo()
        // this.updateProducts()

    }


    finishProductPost = () => {
        this.updateProducts()
        this.getCartInfo()
    }

    render() {
        console.log(this.state)
        return (
            <Container as="section">
                <Row style={{justifyContent: 'center', }}>
                    <Row style={{margin: '10%'}}>
                        <h1>Your Products {this.state.category} </h1>
                    </Row>

                    <Table bordered >
                        <thead>
                            <tr>
                                <th>Product</th>
                                <th>Unit Price</th>
                                <th>Quantity</th>
                                <th>Subtotal</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                                <td><img src={this.props.cart.img} alt={this.state.name}></img></td>
                                <td>Mark</td>
                                <td>Otto</td>
                                <td>@mdo</td>
                            </tr>
                        </tbody>
                    </Table>
                </Row>
                <Link to="/products" className="btn btn-dark">Keep Buying</Link>

            </Container>
        )
    }
}

export default CartList
